mapper.select:
SELECT id, source_url, platform_code, source_type, status, created_at, updated_at
  FROM crawl_task
 WHERE id = #{id}

SELECT id, source_url, platform_code, source_type, status, created_at, updated_at
  FROM crawl_task
 WHERE source_url = #{sourceUrl}
   AND platform_code = #{platformCode}
